mapper.select:
SELECT model.id, model.`name`, model.type_id typeId, model.manufacturer_id manufacturerId, sccj.sccjmc manufacturerName, sbfl.sblxmc typeName FROM bus_power_dev_model model LEFT JOIN t_dw_bzzx_sccj sccj ON model.manufacturer_id = sccj.sccjbm LEFT JOIN t_dw_bzzx_sbfl sbfl ON model.type_id = sbfl.sblxbm
and model.id = #{id}
and model.name like concat(concat('%',#{name}),'%')

SELECT count(0) FROM bus_power_dev_model model LEFT JOIN t_dw_bzzx_sccj sccj ON model.manufacturer_id = sccj.sccjbm LEFT JOIN t_dw_bzzx_sbfl sbfl ON model.type_id = sbfl.sblxbm
and model.id = #{id}
and model.`name` like concat(concat('%',#{name}),'%')
