SELECT t.* FROM t_zh_ztjx_symbxb t LEFT JOIN t_zh_ztjx_symbxm t1 ON t.mbxmid = t1.obj_id
and t.obj_id= #{objId}
and t.syxb like concat(concat('%',#{syxb}),'%')

